function displayStatusSuccess(msg) {
  document.getElementById("alertSuccess").innerText = msg;
  document.getElementById("success").style.display = "block";
  setTimeout(() => {
    document.getElementById("alertSuccess").innerText = "";
    document.getElementById("success").style.display = "none";
  }, 3000);
}

function displayStatusError(msg) {
  document.getElementById("alertError").innerText = msg;
  document.getElementById("error").style.display = "block";
  setTimeout(() => {
    document.getElementById("alertError").innerText = "";
    document.getElementById("error").style.display = "none";
  }, 3000);
}

const BASEURL = "http://127.0.0.1:5000";
const VERSION = "v1";
const AUTH_API_PREFIX = `/api/${VERSION}/auth`;
const STUDENT_API_PREFIX = `/api/${VERSION}/student`;


export { displayStatusError, displayStatusSuccess, BASEURL, VERSION, AUTH_API_PREFIX, STUDENT_API_PREFIX, };
