import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    user: {
      user_id: "",
      role: "",
      first_name: "",
      last_name: "",
      email: "",
    },
    web_token: "",
    token_expiry_on: 0,
    logged_status: false,
  },
  getters: {
    get_user: function (state) {
      return state.user
    },
    get_user_id: function (state) {
      return state.user.user_id
    },
    get_web_token: function (state) {
      return state.web_token
    },
    get_token_expiry_on: function (state) {
      return state.token_expiry_on
    },
    get_logged_status: function (state) {
      return state.logged_status
    },

  },
  mutations: {
    initialiseStore(state) {
			// Check if the ID exists
			if(localStorage.getItem('store')) {
				// Replace the state object with the stored item
				this.replaceState(
					Object.assign(state, JSON.parse(localStorage.getItem('store')))
				);
			}
		},
    set_user(state, payload) {
      // payload is user object 
      state.user = payload
    }
  },
  actions: {
    set_user(context, payload) {
      context.commit('set_user', payload)
    }
  },
  modules: {
  },
});
